mapper.delete:
DELETE
FROM docshare

    ID = #{ID}

        AND submitter = #{userID}

mapper.select:
SELECT docshare.ID, docshare.title, docshare.`desc`, docshare.docPath, docshare.docSize, docshare.uploadTime,
docshare.submitter, facultyDO.userName
FROM docshare LEFT JOIN facultyDO ON docshare.submitter = facultyDO.userID


        ID = #{ID}


        AND title = #{title}


        AND desc = #{desc}


        AND docPath = #{docPath}


        AND docSize = #{docSize}


        AND uploadTime = #{uploadTime}


        AND submitter = #{submitter}

SELECT ID, title, `desc`, docPath, docSize, uploadTime, submitter
FROM docshare
WHERE ID = #{ID}
