mapper.update:
UPDATE docshare


        title = #{title}


        AND desc = #{desc}


        AND docPath = #{docPath}


        AND docSize = #{docSize}


        AND uploadTime = #{uploadTime}


        AND submitter = #{submitter}


WHERE ID = #{ID}

mapper.select:
SELECT ID, title, `desc`, docPath, docSize, uploadTime, submitter
FROM docshare


        ID = #{ID}


        AND title = #{title}


        AND desc = #{desc}


        AND docPath = #{docPath}


        AND docSize = #{docSize}


        AND uploadTime = #{uploadTime}


        AND submitter = #{submitter}

SELECT ID, title, `desc`, docPath, docSize, uploadTime, submitter
FROM docshare
WHERE ID = #{ID}
